@Component({
  selector: 'app-hint',
  templateUrl: './hint.component.html',
  styleUrls: ['./hint.component.css']
})
export class HintComponent {
  @Input() hintText: any;
}
<p>
  <img src="../assets/images/hint.jpg" height="30px"/> {{hintText}}
</p>
